package day1;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 1;

		if (x > 10)
		{
		    System.out.println("x is greater than 10");
		} 
		else if (x < 10) 
		{
		    System.out.println("x is less than 10");
		} 
		else 
		{
		    System.out.println("x is equal to 10");
		}


		

	}

}
